function ValidateLogin(){if($("#txtLoginName").val()=="")return $("#spnLoginName").text("Please enter Login Name"),!1;if($("#txtLoginPassword").val()=="")return $("#spnLoginPassword").text("Please enter Password"),!1;$.ajax({type:"Post",url:"/Account/ValidateLogin",cache:!1,dataType:"json",data:{username:$("#txtLoginName").val(),password:$("#txtLoginPassword").val()},success:function(n){if(n=="success")SuccessMessage(n,"/Account/Dashboard");else $("#txtLoginName").val(""),$("#txtLoginPassword").val(""),ErrorMessage("Invalid Credentials")}})}